import { useEffect } from "react";
import { useState } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";





const Cart = () => {

  const {cart} = useSelector((state)=> state);
  const [totalAmount,setTotalAmount]= useState(0);

  useEffect(() => {
    setTotalAmount(cart.reduce ( (acc,curr)=>(acc + curr.price),0 ));
   
  }, [cart])
  //whenever there are changes in the cart setTotalamount it will have a 
  //reducer fn which will include the acc and the curr.price will be added 
  //to it
  


  return( 
    <div>
     {
      cart.length >0 ?
      (<div>
         <div>
          {
            cart.map ((item,index)=>(
              <CartItem key={item.id} item={item} itemIndex={index} />
            ))
          }
        </div>

        <div>

         
         <div>
              <div>Your cart</div>
              <div>Summary</div>
              <p>
                <span>Total Items:{cart.length}</span>
              </p>
         </div>

         <div>
          <p>Total Amount : ${totalAmount}</p>
          <button>
            Checkout now
          </button>
         </div>


        </div>
      </div>) :
       (
        <div>
          <h1>Cart empty</h1>
          <Link to={"/"}>
            <button>
              Shop Now
            </button>
          </Link>
        </div>
       )

     }
      
    </div>
  );
};

export default Cart;


