import { useDispatch, useSelector } from "react-redux";
import { toast } from 'react-toastify';
import {add,remove} from '../'


const {cart}= useSelector((state)=>state);
//dispatch used because things to be handled inside cart
const dispatch= useDispatch();

const addToCart = () => {
  dispatch(add(post));
  toast.success("Item added to cart");
}

const removeFromCart = () => {
  dispatch(remove(post.id))
  toast.success("Item removed from cart");
}




const Product = ({post}) => {
  return (
    <div>
      <div>
        <p>{post.title}</p>
      </div>
      <div>
        <p>{post.description}</p>
      </div>
      <div>
        <img src={post.image}/>
      </div>
      <div>
        <p>{post.price}</p>
      </div>
      
      {
        //for any one elemnt in the cart say p ka id becomes == to post.id
        //remove the item else add to cart 
        //to remove and add we create functions
        cart.some((p)=> p.id == post.id) ?
        (<button onClick={removeFromCart}>
          Remove Item 
        </button>):
        (<button onClick={addToCart}>
          Add to cart
        </button>)

        
      }

    </div>
  );
};

export default Product;
