import { useDispatch, useSelector } from "react-redux";
import { toast } from 'react-hot-toast';
import {add,remove} from '../redux/Slices/CartSlice';



const Product = ({post}) => {
const {cart}= useSelector((state)=>state);
//dispatch used because things to be handled inside cart
const dispatch= useDispatch();

const addToCart = () => {
  dispatch(add(post));
  toast.success("Item added to cart");
}

const removeFromCart = () => {
  dispatch(remove(post.id))
  toast.success("Item removed from cart");
}


return (
    <div className="flex flex-col items-center justify-between hover:scale-11">
      <div>
        <p>{post.title}</p>
      </div>
      <div>
        <p>{post.description.split(" ").slice(0, 10).join(" ") + "..."}</p>
      </div>
      <div>
        <img src={post.image}/>
      </div>
      <div>
        <p>{post.price}</p>
      </div>
      
      {
        //for any one elemnt in the cart say p ka id becomes == to post.id
        //remove the item else add to cart 
        //to remove and add we create functions
        cart.some((p)=> p.id == post.id) ?
        (<button onClick={removeFromCart}>
          Remove Item 
        </button>):
        (<button onClick={addToCart}>
          Add to cart
        </button>)

        
      }

    </div>
  );
};

export default Product;
