import { useSelector } from "react-redux";


const {cart}= useSelector((state)=>state);


const Product = ({post}) => {
  return (
    <div>
      <div>
        <p>{post.title}</p>
      </div>
      <div>
        <p>{post.description}</p>
      </div>
      <div>
        <img src={post.image}/>
      </div>
      <div>
        <p>{post.price}</p>
      </div>
      
      {
        cart.some((p)=> p.id == post.id) ?
        (<button onClick={removeFromCart}>
          Remove Item 
        </button>):
        (<button onClick={addToCart}>
          Add to cart
        </button>)

        
      }

    </div>
  );
};

export default Product;
