// store.js
import { configureStore } from "@reduxjs/toolkit";
import cartReducer from "./Slices/CartSlice"; // Import the reducer function, not the slice object

export const store = configureStore({
    reducer: {
        cart: cartReducer, // Pass the reducer function
    },
});
